class CountMetadataLangTest(unittest.TestCase):
    def setUp(self):
        self.test_dir = join("test", "jalc")
        self.sample_dump_dir = join(self.test_dir, "JOCI_PRE_SAMPLE")
        self.csv_file = join(self.test_dir, "sample_count.csv")

    def test_count_publisher_lang(self):
        my_dict = CountMetadataLang()
        results_citing = my_dict.count_publisher_lang(source_dict_publisher_citing, citing=True)
        en_ja = results_citing[0]
        en = results_citing[1]
        ja = results_citing[2]
        self.assertEqual(1, en_ja)
        self.assertEqual(1, en)
        self.assertEqual(1, ja)
        results_cited = my_dict.count_publisher_lang(source_dict_publisher_cited, citing=False)
        en_ja_cited = results_cited[0]
        en_cited = results_cited[1]
        ja_cited = results_cited[2]
        self.assertEqual(0, en_ja_cited)
        self.assertEqual(1, en_cited)
        self.assertEqual(1, ja_cited)

    def test_count_journal_title_lang(self):
        my_dict = CountMetadataLang()
        results_citing = my_dict.count_journal_title_lang(source_dict_publisher_citing, citing=True)
        en_ja = results_citing[0]
        en = results_citing[1]
        ja = results_citing[2]
        self.assertEqual(2, en_ja)
        self.assertEqual(0, en)
        self.assertEqual(0, ja)
        results_cited = my_dict.count_journal_title_lang(source_dict_journal_title_cited, citing=False)
        en_ja_cited = results_cited[0]
        en_cited = results_cited[1]
        ja_cited = results_cited[2]
        self.assertEqual(0, en_ja_cited)
        self.assertEqual(2, en_cited)
        self.assertEqual(2, ja_cited)
    
    def test_count_title_lang(self):
        my_dict = CountMetadataLang()
        results_citing = my_dict.count_title_lang(source_dict_publisher_citing, citing=True)
        en_ja = results_citing[0]
        en = results_citing[1]
        ja = results_citing[2]
        self.assertEqual(1, en_ja)
        self.assertEqual(0, en)
        self.assertEqual(2, ja)
        results_cited = my_dict.count_title_lang(source_dict_title_cited, citing=False)
        en_ja_cited = results_cited[0]
        en_cited = results_cited[1]
        ja_cited = results_cited[2]
        self.assertEqual(0, en_ja_cited)
        self.assertEqual(4, en_cited)
        self.assertEqual(0, ja_cited)

    def test_count_creator_names_lang(self):
        my_dict = CountMetadataLang()
        results_citing = my_dict.count_creator_names_lang(source_dict_creators, citing=True)
        en_ja = results_citing[0]
        en = results_citing[1]
        ja = results_citing[2]
        self.assertEqual(7, en_ja)
        self.assertEqual(0, en)
        self.assertEqual(0, ja)
        results_cited = my_dict.count_creator_names_lang(source_dict_creators, citing=False)
        en_ja_cited = results_cited[0]
        en_cited = results_cited[1]
        ja_cited = results_cited[2]
        self.assertEqual(9, en_ja_cited)
        self.assertEqual(0, en_cited)
        self.assertEqual(3, ja_cited)

    def test_call_functions_for_all_zips(self):
        for el in os.listdir(self.sample_dump_dir):
            if el.endswith("decompr_zip_dir"):
                shutil.rmtree(os.path.join(self.sample_dump_dir, el))
        my_dict = CountMetadataLang()
        all_zips = my_dict.find_zip_subfiles(self.sample_dump_dir)
        my_dict.call_functions_for_all_zips(all_zips, [my_dict.count_publisher_lang, my_dict.count_title_lang, my_dict.count_journal_title_lang, my_dict.count_creator_names_lang], self.csv_file, True, True)
        publisher_count = ['3', '0', '0', '0', '0', '0']
        target_row0 = 0
        title = ['1', '2', '0', '0', '3', '3']
        target_row1 = 1
        journal_title = ['3', '0', '0', '0', '0', '3']
        target_row2 = 2
        creators = ['12', '4', '0', '11', '5', '6']
        target_row3 = 3
        with open(self.csv_file, 'r') as csv_file:
            csvreader = csv.reader(csv_file)
            next(csvreader)
            for idx, row in enumerate(csvreader, start=0):
                if idx == target_row0:
                    self.assertEqual(publisher_count, row)
                elif idx == target_row1:
                    self.assertEqual(title, row)
                elif idx == target_row2:
                    self.assertEqual(journal_title, row)
                elif idx == target_row3:
                    self.assertEqual(creators, row)
        for el in os.listdir(self.sample_dump_dir):
            if el.endswith("decompr_zip_dir"):
                shutil.rmtree(os.path.join(self.sample_dump_dir, el))

